import React, { useState } from "react";
import styles from "../styles/App.module.css";
import { fetchAdvice } from "../api";

function UserForm({ setUserData, setIsActive, setChatHistory, adviceType }) {
  const [form, setForm] = useState({
    topic: "",
    age: "",
    profession: "",
    extra: ""
  });
  const [submitted, setSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLoading) return;

    // Валидация
    if (!form.topic || !form.age || !form.profession) {
      alert("Пожалуйста, заполните обязательные поля.");
      return;
    }
    if (form.age < 10 || form.age > 120) {
      alert("Возраст должен быть от 10 до 120 лет.");
      return;
    }

    setIsLoading(true);
    try {
      setUserData(form);
      setIsActive(true);

      const advice = await fetchAdvice({
        style: adviceType,
        topic: form.topic,
        age: form.age,
        profession: form.profession,
        extra: form.extra,
        user_message: "Какой совет вы можете дать?"
      });

      setChatHistory((prev) => [...prev, { role: "ai", content: advice }]);
      setSubmitted(true);
    } catch (error) {
      console.error("Ошибка при получении совета:", error);
      alert("Не удалось получить совет. Пожалуйста, попробуйте позже.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={styles.userform}>
      <h3>Персонализация</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Тема:
          <input
            type="text"
            name="topic"
            value={form.topic}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Возраст:
          <input
            type="number"
            name="age"
            value={form.age}
            onChange={handleChange}
            required
            min="10"
            max="120"
          />
        </label>
        <label>
          Профессия:
          <input
            type="text"
            name="profession"
            value={form.profession}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Доп. информация:
          <textarea
            name="extra"
            value={form.extra}
            onChange={handleChange}
            rows={3}
          />
        </label>
        <button 
          type="submit" 
          disabled={submitted || isLoading}
          aria-live="polite"
        >
          {isLoading ? "Отправка..." : submitted ? "Данные сохранены" : "Сохранить"}
        </button>
        {submitted && (
          <button 
            type="button" 
            onClick={() => setSubmitted(false)}
            className={styles.editButton}
          >
            Изменить данные
          </button>
        )}
      </form>
    </div>
  );
}

export default UserForm;
