import React, { useState } from "react";
import styles from "../styles/App.module.css";
import { fetchAdvice } from "../api";

function UserForm({ setUserData, setIsActive, setChatHistory }) {
  const [form, setForm] = useState({
    topic: "",
    age: "",
    profession: "",
    extra: ""
  });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (form.topic && form.age && form.profession) {
      setUserData(form);
      setIsActive(true);
      setSubmitted(true);

      // Получаем совет от AI
      try {
        const advice = await fetchAdvice({
          style: "Мотивация", // Здесь можно использовать выбранный стиль
          topic: form.topic,
          age: form.age,
          profession: form.profession,
          extra: form.extra,
          user_message: "Какой совет вы можете дать?" // Пример сообщения
        });

        // Добавляем ответ в историю чата
        setChatHistory((prev) => [...prev, { role: "ai", content: advice }]);
      } catch (error) {
        console.error("Ошибка при получении совета:", error);
      }
    }
  };

  return (
    <div className={styles.userform}>
      <h3>Персонализация</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Тема:
          <input
            type="text"
            name="topic"
            value={form.topic}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Возраст:
          <input
            type="number"
            name="age"
            value={form.age}
            onChange={handleChange}
            required
            min="10"
            max="120"
          />
        </label>
        <label>
          Профессия:
          <input
            type="text"
            name="profession"
            value={form.profession}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Доп. информация:
          <textarea
            name="extra"
            value={form.extra}
            onChange={handleChange}
            rows={3}
          />
        </label>
        <button type="submit" disabled={submitted}>
          {submitted ? "Данные сохранены" : "Сохранить"}
        </button>
      </form>
    </div>
  );
}

export default UserForm;
